package fiap.tokio.logica.exercicios;

public class Aula05SaídaDeDados {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float nota = 8.0f;
	
		
		
		System.out.println("O Miguel tirou: " + nota);
		System.out.printf("O Miguel tirou: %.2f \n", nota);
		System.out.format("O MIguel tirou: %.2f\n", nota);
		
		String nome = "Arthur";
		
		System.out.printf("O %s tirou: %.3f", nome, nota);
		

		
	}

}
