package fiap.tokio.logica.exercicios;

public class Aulao5Tipos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float preco = 10.99f;
		float preco2 = (float) 12.99;
		
		System.out.println("Valor do produto: R$" + preco);
		
		char letra = 'J';
		System.out.println(letra);

		boolean ligado = true;
		System.out.println(ligado);
		
		boolean desligado = (boolean) false;
		System.out.println (desligado);
		
		double precoDouble = 10.99;
		System.out.println(precoDouble);
		
		
	}

}
